mapper.update:
  		UPDATE free_board                       					         
			   SET bo_title    = #{boTitle}	 
			     , bo_writer   = #{boWriter}        
			     , bo_category = #{boCategory}	 
			     , bo_content  = #{boContent}  
			     , bo_mod_date = SYSDATE  					 
			 WHERE bo_no = #{boNo}

mapper.delete:

 			UPDATE free_board      
			   SET bo_del_yn = 'Y' 
			 WHERE bo_no     = #{boNo}

mapper.update:
  	UPDATE free_board        
			 SET bo_hit = bo_hit + 1 
		 WHERE bo_no  = #{boNo}
