mapper.update:
  		UPDATE question_board                       					         
			   SET bo_title    = #{boTitle}	 
			     , bo_writer   = #{boWriter}        
			     , bo_category = #{boCategory}	 
			     , bo_content  = #{boContent}  
			     , bo_mod_date = SYSDATE  					 
			 WHERE bo_no = #{boNo}
