mapper.select:
	SELECT COUNT(*)
     FROM reply
	 WHERE re_category = #{reCategory}
	   AND re_parent_no = #{reParentNo}

mapper.insert:
	INSERT INTO reply (
      re_no    , re_category    , re_parent_no
    , re_mem_id    , re_content    , re_ip
) VALUES (   
      seq_reply.nextval, #{reCategory}    , #{reParentNo}
    , #{reMemId}       , #{reContent}     , #{reIp}
)

mapper.update:
UPDATE reply
	SET re_content = #{reContent}
	   , re_mod_date = SYSDATE
 WHERE re_no = ${reNo}
